function makeTextBox(text, editable){
    let textBox = document.createElement('div');
    if(editable) {
        textBox.setAttribute('contenteditable', 'true');
        textBox.style.background = 'lightgray';
    }
    textBox.innerText = text;
    app().append(textBox);

    return textBox;
}
let content = `복사는 단축키 ctrl + c를 사용하거나,
잘라낼 요소를 선택 후,
마우스 우클릭 [복사] 메뉴 선택으로 할 수 있습니다.

텍스트 중 일부를 복사해 보세요.`;
let script = makeTextBox(content);

