content.text:
form.onsubmit = e => {
    let checks = document.termsForm.agree;
    for(var i=0; i<checks.length; i++) {
        if(!checks[i].checked) {
            alert('모두 체크해주세요.');
            break;
        }
    }
    return false;
};

