prevMonth(() => {
    if (month === 0) {
		month = 11;
		year--;
	} else {
		month--;
	}
	updateCalendar();
});
nextMonth(() => {
    if (month === 11) {
		month = 0;
		year++;
	} else {
		month++;
	}
	updateCalendar();
});
